import { ethers } from 'ethers';
// Đảm bảo đường dẫn import đúng file JSON bạn tạo ở bước dưới
import ScholarshipArtifact from '../contracts/Scholarship.json'; 

// Thay địa chỉ này bằng địa chỉ bạn nhận được khi chạy: npx hardhat run scripts/deploy.js
const CONTRACT_ADDRESS = "0x5FbDB2315678afecb367f032d93F642f64180aa3"; 

export const getEthereumObject = () => window.ethereum;

export const connectWallet = async () => {
    if (!getEthereumObject()) return alert("Hãy cài đặt MetaMask!");
    // Ethers v6 dùng BrowserProvider
    const provider = new ethers.BrowserProvider(window.ethereum);
    const accounts = await provider.send("eth_requestAccounts", []);
    return accounts[0];
};

export const getContract = async () => {
    if (!getEthereumObject()) return null;
    const provider = new ethers.BrowserProvider(window.ethereum);
    const signer = await provider.getSigner(); 
    // Khởi tạo contract với Signer để có thể gửi transaction (write)
    const contract = new ethers.Contract(CONTRACT_ADDRESS, ScholarshipArtifact.abi, signer);
    return contract;
};
